// Template Discovery and Analysis
const repoData = $node['Scan Repository'].json;
const tree = repoData.tree || [];

// Find all template files
const templateFiles = tree.filter(item => {
  return item.type === 'blob' && 
         item.path.startsWith('templates/') && 
         item.path.endsWith('.txt') &&
         !item.path.includes('/.') && // Exclude hidden files
         !item.path.includes('_backup') && // Exclude backup files
         !item.path.includes('_draft'); // Exclude draft files
});

// Categorize templates
const categories = {};
const templateMetadata = [];

templateFiles.forEach(file => {
  const pathParts = file.path.split('/');
  if (pathParts.length >= 3) {
    const category = pathParts[1];
    const filename = pathParts[pathParts.length - 1];
    const templateName = filename.replace('.txt', '');

    // Extract version from filename if present
    const versionMatch = templateName.match(/_v(\d+)\.(\d+)\.(\d+)$/);
    let version = '1.0.0';
    let baseName = templateName;

    if (versionMatch) {
      version = `${versionMatch[1]}.${versionMatch[2]}.${versionMatch[3]}`;
      baseName = templateName.replace(/_v\d+\.\d+\.\d+$/, '');
    }

    if (!categories[category]) {
      categories[category] = [];
    }

    const metadata = {
      name: baseName,
      filename: filename,
      category: category,
      version: version,
      path: file.path,
      size: file.size,
      sha: file.sha,
      url: file.url,
      download_url: `https://raw.githubusercontent.com/bader1919/n8n-claude-prompt-system/main/${file.path}`,
      last_modified: new Date().toISOString(), // We'll get actual date later
      discovered_at: new Date().toISOString()
    };

    categories[category].push(metadata);
    templateMetadata.push(metadata);
  }
});

// Statistics
const stats = {
  total_templates: templateMetadata.length,
  categories_count: Object.keys(categories).length,
  categories: Object.keys(categories),
  new_templates: [], // Will be determined by comparing with registry
  updated_templates: [],
  scan_timestamp: new Date().toISOString()
};

return {
  discovered_templates: templateMetadata,
  categories: categories,
  stats: stats,
  step: 'template_discovery'
};

// Compare with Current Registry and Identify Changes
const discoveredData = $node['Analyze Templates'].json;
const currentRegistry = $node['Load Current Registry'].json;

const discoveredTemplates = discoveredData.discovered_templates;
const currentTemplates = currentRegistry.templates || {};

const changes = {
  new_templates: [],
  updated_templates: [],
  new_versions: [],
  deprecated_templates: [],
  category_changes: []
};

// Group discovered templates by base name
const templateGroups = {};
discoveredTemplates.forEach(template => {
  if (!templateGroups[template.name]) {
    templateGroups[template.name] = [];
  }
  templateGroups[template.name].push(template);
});

// Analyze each template group
Object.entries(templateGroups).forEach(([templateName, versions]) => {
  const currentTemplate = currentTemplates[templateName];

  if (!currentTemplate) {
    // Completely new template
    changes.new_templates.push({
      name: templateName,
      versions: versions,
      category: versions[0].category
    });
  } else {
    // Existing template - check for new versions
    const currentVersions = Object.keys(currentTemplate.versions || {});
    const discoveredVersions = versions.map(v => v.version);

    const newVersions = discoveredVersions.filter(v => !currentVersions.includes(v));

    if (newVersions.length > 0) {
      changes.new_versions.push({
        name: templateName,
        new_versions: newVersions,
        versions_data: versions.filter(v => newVersions.includes(v.version))
      });
    }

    // Check for category changes
    if (currentTemplate.category !== versions[0].category) {
      changes.category_changes.push({
        name: templateName,
        old_category: currentTemplate.category,
        new_category: versions[0].category
      });
    }
  }
});

// Check for deprecated templates (exist in registry but not in repo)
Object.keys(currentTemplates).forEach(templateName => {
  if (!templateGroups[templateName]) {
    changes.deprecated_templates.push({
      name: templateName,
      last_version: currentTemplates[templateName].current_version
    });
  }
});

const summary = {
  total_changes: Object.values(changes).reduce((sum, arr) => sum + arr.length, 0),
  has_changes: Object.values(changes).some(arr => arr.length > 0),
  change_types: Object.keys(changes).filter(key => changes[key].length > 0)
};

return {
  template_groups: templateGroups,
  changes: changes,
  summary: summary,
  current_registry: currentRegistry,
  discovered_stats: discoveredData.stats,
  step: 'change_detection'
};

// Process New and Updated Templates
const changeData = $node['Detect Changes'].json;
const changes = changeData.changes;
const templateGroups = changeData.template_groups;

const processingResults = [];

// Function to fetch and analyze template content
async function analyzeTemplate(template) {
  try {
    const response = await fetch(template.download_url);
    const content = await response.text();

    // Extract variables from template
    const variablePattern = /\{\{(\w+)\}\}/g;
    const variables = [];
    let match;

    while ((match = variablePattern.exec(content)) !== null) {
      if (!variables.includes(match[1])) {
        variables.push(match[1]);
      }
    }

    // Estimate quality score based on various factors
    const qualityFactors = {
      has_description: content.includes('You are') ? 1 : 0,
      has_context: content.includes('Context:') ? 1 : 0,
      has_instructions: content.includes('Instructions:') ? 1 : 0,
      has_output_format: content.includes('Output') || content.includes('Format') ? 1 : 0,
      variable_count: Math.min(variables.length / 10, 1), // Normalize to 0-1
      content_length: Math.min(content.length / 2000, 1), // Normalize to 0-1
      has_examples: content.includes('example') || content.includes('Example') ? 1 : 0
    };
    \
    const qualityScore = (Object.values(qualityFactors).reduce((sum, val) => sum + val, 0) / Object.keys(qualityFactors).length) * 10;
    \
    return {
      template: template,
      analysis: {
        content_length: content.length,
        variable_count: variables.length,
        variables: variables,
        estimated_tokens: Math.ceil(content.length / 4),
        quality_score: Math.round(qualityScore * 10) / 10,
        quality_factors: qualityFactors,
        has_validation: variables.length > 0
      },
      content: content.substring(0, 500) + (content.length > 500 ? '...' : '') // Truncated content for logging
    };
  } catch (error) {
    return {
      template: template,
      error: error.message,
      analysis: null
    };
  }
}

// Process all templates that need analysis
const templatesToAnalyze = [];

// Add new templates
changes.new_templates.forEach(newTemplate => {
  newTemplate.versions.forEach(version => {
    templatesToAnalyze.push(version);
  });
});

// Add new versions
changes.new_versions.forEach(versionChange => {
  versionChange.versions_data.forEach(version => {
    templatesToAnalyze.push(version);
  });
});

// Since we can't use async/await in n8n code node, we'll prepare the data for the next node
return {
  templates_to_analyze: templatesToAnalyze,
  changes: changes,
  current_registry: changeData.current_registry,
  step: 'template_analysis_prep'
};

// Build Updated Registry
const analysisData = $node['Prepare Analysis'].json;
const currentRegistry = analysisData.current_registry;
const changes = analysisData.changes;
const templatesToAnalyze = analysisData.templates_to_analyze;

// Clone current registry
const updatedRegistry = JSON.parse(JSON.stringify(currentRegistry));

// Update registry version
const registryVersion = updatedRegistry.registry_version || '1.0.0';
const versionParts = registryVersion.split('.').map(Number);
versionParts[2]++; // Increment patch version
updatedRegistry.registry_version = versionParts.join('.');
updatedRegistry.last_updated = new Date().toISOString();

// Helper function to create template entry
function createTemplateEntry(template, analysis) {
  const estimated_analysis = analysis || {
    content_length: 500,
    variable_count: 5,
    variables: [],
    estimated_tokens: 125,
    quality_score: 8.0
  };
  \
  return {
    name: template.name.replace(/_/g, ' ').replace(/ \w/g, l => l.toUpperCase()),
    description: `Generated template for ${template.category.replace(/_/g, ' ')}`,
    category: template.category,
    current_version: template.version,
    versions: {
      [template.version]: {
        file_path: template.path,
        created_date: template.discovered_at,
        author: 'auto-discovery',
        changelog: 'Auto-discovered template',
        status: 'active',
        compatibility: ['anthropic', 'openai', 'groq'],
        estimated_tokens: estimated_analysis.estimated_tokens,
        quality_score: estimated_analysis.quality_score
      }
    },
    usage_stats: {
      total_executions: 0,
      success_rate: 0,
      avg_response_time: 0,
      cost_per_execution: 0
    },
    tags: [template.category.replace(/_/g, '-'), 'auto-discovered'],
    required_variables: estimated_analysis.variables || [],
    optional_variables: []
  };
}

// Process new templates
changes.new_templates.forEach(newTemplate => {
  const latestVersion = newTemplate.versions.sort((a, b) => b.version.localeCompare(a.version))[0];
  const templateEntry = createTemplateEntry(latestVersion);
  \
  // Add all versions
  newTemplate.versions.forEach(version => {
    templateEntry.versions[version.version] = {
      file_path: version.path,
      created_date: version.discovered_at,
      author: 'auto-discovery',
      changelog: `Auto-discovered version ${version.version}`,
      status: version.version === latestVersion.version ? 'active' : 'deprecated',
      compatibility: ['anthropic', 'openai', 'groq'],
      estimated_tokens: 125, // Default estimate
      quality_score: 8.0 // Default score
    };
  });
  \
  updatedRegistry.templates[newTemplate.name] = templateEntry;
});

// Process new versions of existing templates
changes.new_versions.forEach(versionChange => {
  const templateName = versionChange.name;
  const existingTemplate = updatedRegistry.templates[templateName];
  \
  if (existingTemplate) {
    versionChange.versions_data.forEach(version => {
      existingTemplate.versions[version.version] = {
        file_path: version.path,
        created_date: version.discovered_at,
        author: 'auto-discovery',
        changelog: `Auto-discovered version ${version.version}`,
        status: 'active',
        compatibility: ['anthropic', 'openai', 'groq'],
        estimated_tokens: 125,
        quality_score: 8.0
      };
    });
    \
    // Update current version to latest
    const allVersions = Object.keys(existingTemplate.versions);
    const latestVersion = allVersions.sort((a, b) => b.localeCompare(a))[0];
    existingTemplate.current_version = latestVersion;
  }
});

// Mark deprecated templates
changes.deprecated_templates.forEach(deprecated => {
  const templateName = deprecated.name;
  if (updatedRegistry.templates[templateName]) {
    const versions = updatedRegistry.templates[templateName].versions;
    Object.keys(versions).forEach(version => {
      versions[version].status = 'deprecated';
    });
  }
});

// Update discovery settings
if (!updatedRegistry.discovery_settings) {
  updatedRegistry.discovery_settings = {};
}
updatedRegistry.discovery_settings.last_scan = new Date().toISOString();
updatedRegistry.discovery_settings.templates_discovered = Object.keys(updatedRegistry.templates).length;

// Generate summary
const updateSummary = {
  registry_version: updatedRegistry.registry_version,
  total_templates: Object.keys(updatedRegistry.templates).length,
  changes_applied: {
    new_templates: changes.new_templates.length,
    new_versions: changes.new_versions.length,
    deprecated_templates: changes.deprecated_templates.length,
    category_changes: changes.category_changes.length
  },
  scan_timestamp: new Date().toISOString()
};

return {
  updated_registry: updatedRegistry,
  update_summary: updateSummary,
  changes_applied: changes,
  step: 'registry_update'
};

// Generate Commit Message and Prepare Update
const updateData = $node['Update Registry'].json;
const summary = updateData.update_summary;
const changes = updateData.changes_applied;

// Build descriptive commit message
let commitMessage = `Auto-update template registry v${summary.registry_version}

`;

if (changes.new_templates.length > 0) {
  commitMessage += `âœ¨ New templates (${changes.new_templates.length}):
`;
  changes.new_templates.forEach(template => {
    commitMessage += `  - ${template.name} (${template.category})
`;
  });
  commitMessage += '
';
}

if (changes.new_versions.length > 0) {
  commitMessage += `ðŸ“ New versions (${changes.new_versions.length}):
`;
  changes.new_versions.forEach(version => {
    commitMessage += `  - ${version.name}: ${version.new_versions.join(', ')}
`;
  });
  commitMessage += '
';
}

if (changes.deprecated_templates.length > 0) {
  commitMessage += `ðŸ—‘ï¸ Deprecated templates (${changes.deprecated_templates.length}):
`;
  changes.deprecated_templates.forEach(template => {
    commitMessage += `  - ${template.name}
`;
  });
  commitMessage += '
';
}

commitMessage += `Total templates: ${summary.total_templates}
`;
commitMessage += `Scan time: ${summary.scan_timestamp}
`;
commitMessage += '
Generated by n8n auto-discovery workflow';

// Prepare file update
const fileContent = JSON.stringify(updateData.updated_registry, null, 2);

return {
  file_content: fileContent,
  commit_message: commitMessage,
  update_summary: summary,
  should_commit: true,
  step: 'commit_preparation'
};

// No Changes Summary
const changeData = $node['Detect Changes'].json;

return {
  message: 'No template changes detected',
  scan_summary: {
    total_templates_scanned: Object.keys(changeData.template_groups).length,
    categories: changeData.discovered_stats.categories,
    scan_timestamp: changeData.discovered_stats.scan_timestamp,
    next_scan: new Date(Date.now() + 6 * 60 * 60 * 1000).toISOString() // 6 hours from now
  },
  step: 'no_changes'
};
